import React from "react";

const PageButton = ({
  text,
  details,
  totalPages,
  activePage,
  setActivePage,
}) => {
  const handleNext = () => {
    if (activePage < totalPages) {
      setActivePage(activePage + 1);
    }
  };

  const handlePrev = () => {
    if (activePage > 1) {
      setActivePage(activePage - 1);
    }
  };

  return (
    <div>
      <button
        className={`${details}  text-2xl font-bold border-gray-400 bg-azulgrisclaro hover:bg-azulgris text-black px-5 py-1`}
      >
        {text}
      </button>
    </div>
  );
};

export default PageButton;
