import React, { useEffect, useState } from "react";
import { socket } from "../../../config/socket";
import { IoIosArrowBack, IoIosArrowForward } from "react-icons/io";

const ProductSliderImages = ({ id_product }) => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [dataArrived, setDataArrived] = useState(false);
  const [images, setImages] = useState([]);

  useEffect(() => {
    if (!dataArrived) {
      socket.emit("get_images", id_product);
      function onPostImages(data) {
        setImages(data);
        setDataArrived(true);
        console.log(data);
      }

      socket.on("post_images", (data) => onPostImages(data));
      return () => {
        socket.off("post_images", (data) => onPostImages(data));
      };
    }
  }, [socket]);

  const handleNext = () => {
    if (currentIndex < images.length - 1) setCurrentIndex(currentIndex + 1);
    else setCurrentIndex(0);
  };

  const handlePrev = () => {
    if (currentIndex > 0) setCurrentIndex(currentIndex - 1);
    else setCurrentIndex(images.length - 1);
  };

  return (
    <div className=" flex flex-col justify-center items-center">
      <img
        alt=""
        src={dataArrived ? images[currentIndex].url_image : ""}
        style={{ width: "1000px", height: "600px", objectFit: "contain" }}
        loading="lazy"
      />
      <div className="flex-wrap flex justify-between mt-10">
        <div className=" mr-10">
          <IoIosArrowBack
            size="50"
            color=" grey"
            cursor="pointer"
            onClick={handlePrev}
            className=" transform scale-100 hover:scale-125 transition duration-300 ease-in-out"
          />
        </div>
        <div className=" ml-10">
          <IoIosArrowForward
            size="50"
            color=" grey"
            cursor="pointer"
            onClick={handleNext}
            className=" transform scale-100 hover:scale-125 transition duration-300 ease-in-out"
          />
        </div>
      </div>
    </div>
  );
};

export default ProductSliderImages;
