import React, { useEffect, useState } from "react";
import { socket } from "../../../config/socket";
import { IoIosArrowBack, IoIosArrowForward } from "react-icons/io";

const ProductSliderImages = ({ id_product }) => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [dataArrived, setDataArrived] = useState(false);
  const [images, setImages] = useState([]);

  useEffect(() => {
    if (!dataArrived) {
      socket.emit("get_images", id_product);
      function onPostImages(data) {
        setImages(data);
        setDataArrived(true);
        console.log(images);
      }

      socket.on("post_images", (data) => onPostImages(data));
      return () => {
        socket.off("post_images", (data) => onPostImages(data));
      };
    }
  }, [socket]);

  const handleNext = () => {
    if (currentIndex < images.length) setCurrentIndex(currentIndex + 1);
  };

  const handlePrev = () => {
    if (currentIndex > 1) setCurrentIndex(currentIndex - 1);
  };

  return (
    <div className=" flex flex-col justify-center items-center">
      <img
        alt={images[currentIndex]}
        src={images[currentIndex].url_image}
        style={{ width: "500px", height: "500px", objectFit: "contain" }}
        loading="lazy"
        className=" w-2/3 h-2/3"
      />
      <div className=" flex flex-wrap justify-between items-center">
        <IoIosArrowBack size="50" />
        <IoIosArrowForward size="50" />
      </div>
    </div>
  );
};

export default ProductSliderImages;
