import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import PaginationButtons from "../../utils/custom/PaginationButtons";
import { socket } from "../../../config/socket";

const Auctions = () => {
  const location = useLocation();
  const utils = location.state.utils;

  const [products, setProducts] = useState([]);
  const [active, setActive] = useState(1);
  const [dataArrived, setDataArrived] = useState(false);

  useEffect(() => {
    if (!dataArrived) {
      socket.emit("get_products");

      function onGetProducts(data) {
        setDataArrived(true);
        setProducts(data);
      }
      socket.on("post_products", (data) => onGetProducts(data));
    }

    return () => {
      socket.off("postProducts", (data) => onGetProducts(data));
    };
  }, [socket]);

  return (
    <div className=" w-screen h-screen">
      <PaginationButtons />
    </div>
  );
};

export default Auctions;
