import React from "react";
import PageButton from "./PageButton";

const PaginationButtons = ({ totalPages, activePage, setActivePage }) => {
  const handleNext = () => {
    if (activePage < totalPages) {
      console.log(activePage);
      setActivePage(activePage + 1);
    }
  };

  const handleClick = (e) => {
    console.log(activePage);
    setActivePage(e.id);
  };

  const handlePrev = () => {
    if (activePage > 1) {
      console.log(activePage);
      setActivePage(activePage - 1);
    }
  };
  return (
    <div className=" flex justify-center items-center">
      <div className="absolute bottom-5 flex">
        <PageButton
          text={"← Prev"}
          details="rounded-l-md mr-1"
          totalPages={totalPages}
          activePage={activePage}
          setActivePage={setActivePage}
          handle={handlePrev}
        />
        {totalPages.map((total, index) => {
          return (
            <PageButton
              key={total}
              text={total}
              details={activePage === total ? "bg-azulgris m-2" : ""}
              totalPages={totalPages}
              activePage={activePage}
              setActivePage={setActivePage}
              handle={handleClick}
            />
          );
        })}
        <PageButton
          text={"Next →"}
          details="rounded-r-md ml-1"
          totalPages={totalPages}
          activePage={activePage}
          setActivePage={setActivePage}
          handle={handleNext}
        />
      </div>
    </div>
  );
};

export default PaginationButtons;
