import React from "react";
import Timer from "./Timer";

const ProductCard = ({ nombre, descripcion, precio, marca, modelo, año }) => {
  const horaInicio = new Date(); // Hora de inicio
  horaInicio.setHours(18, 38, 0);
  return (
    <div className=" w-full">
      <div className=" flex  ml-24 mb-10">
        <h2 className=" text-4xl text-gray-900 font-semibold font-[Dosis]">
          {marca.toUpperCase()}
        </h2>
      </div>
      <div className=" flex ml-24 mb-10">
        <h1 className=" text-6xl font-semibold font-[Dosis]">{nombre}</h1>
      </div>
      <div className=" flex ml-24 mb-10">
        <p className=" text-3xl text-gray-400 font-light text-justify">
          {descripcion}
        </p>
      </div>
      <div className=" flex ml-24 mb-20 justify-between">
        <h2 className=" text-4xl font-[Dosis]">USD{precio}</h2>
        <h2 className=" text-4xl font-semibold font-[Dosis]">
          {modelo.toUpperCase()}
        </h2>
        <h2 className=" text-4xl font-[Dosis]">{año}</h2>
      </div>
      <div className=" flex ml-24 mb-20 justify-between">
        <button className="transform scale-100 hover:scale-125 transition duration-300 ease-in-out text-center font-semibold rounded text-white text-4xl px-8 py-2 border-gray-400 border-2 bg-azulgris">
          Offer
        </button>
        <div>
          <Timer horaInicio={horaInicio} />
        </div>
      </div>
    </div>
  );
};

export default ProductCard;
