import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import PaginationButtons from "../../utils/custom/PaginationButtons";
import { socket } from "../../../config/socket";
import Pagination from "../../utils/custom/Pagination";

const Auctions = () => {
  const location = useLocation();
  const utils = location.state.utils;

  const [products, setProducts] = useState([]);
  const [activePage, setActivePage] = useState(1);
  const [dataArrived, setDataArrived] = useState(false);
  const [totalPages, setTotalPages] = useState(
    [...Array(Math.ceil(products.length / 3)).keys()].map((i) => i + 1)
  );

  useEffect(() => {
    if (!dataArrived) {
      socket.emit("get_products");

      function onGetProducts(data) {
        setDataArrived(true);
        setProducts(data);
      }

      socket.on("post_products", (data) => onGetProducts(data));
      return () => {
        socket.off("post_products", (data) => onGetProducts(data));
      };
    }
  }, [socket]);

  return (
    <div className=" w-screen h-screen">
      <Pagination products={products} activePage={activePage} />
      <PaginationButtons totalPages={totalPages} />
    </div>
  );
};

export default Auctions;
