import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router";

const Login = ({ socket }) => {
  const [login, setLogin] = useState({
    email: "",
    password: "",
  });
  const [response, setResponse] = useState("");
  const [message, setMessage] = useState("");
  const [visible, setVisible] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    function onResponse(data) {
      const { response, message } = data;
      console.log(data);
      if (response === "Success") {
        navigate("/auctions");
      } else {
        if (response === "Disconnected") socket.disconnect();
        setResponse(response);
        setMessage(message);
        setVisible(true);
        setTimeout(() => {
          setVisible(false);
        }, 3000);
      }
    }

    socket.on("Login response", (data) => onResponse(data));
    return () => {
      socket.off("Login response", (data) => onResponse(data));
    };
  }, [navigate, socket]);

  const handleChange = (e) => {
    setLogin({
      ...login,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const { email, password } = login;
    if (email !== "" || password !== "") {
      socket.emit("Login", {
        email: email,
        password: password,
      });
    } else {
      setResponse("Preventive");
      setMessage("Incomplete fields, please try again");
      setVisible(true);
      setTimeout(() => {
        setVisible(false);
      }, 3000);
    }
  };

  return <div>Login</div>;
};

export default Login;
