import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import Logo from "../../../images/Logo.jpg";

const Login = ({ socket }) => {
  const [login, setLogin] = useState({
    email: "",
    password: "",
  });
  const [response, setResponse] = useState("");
  const [message, setMessage] = useState("");
  const [visible, setVisible] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    function onResponse(data) {
      const { response, message, nombre, apellido } = data;
      if (response === "Success") {
        const utils = {
          nombre: nombre,
          apellido: apellido,
          socket: socket,
        };
        const destination = {
          pathname: "/auctions",
          state: { utils: utils },
        };
        navigate(`/auctions?${JSON.stringify({ destination })}`);
      } else {
        if (response === "Disconnected") socket.disconnect();
        setResponse(response);
        setMessage(message);
        setVisible(true);
        setTimeout(() => {
          setVisible(false);
        }, 3000);
      }
    }

    socket.on("Login response", (data) => onResponse(data));
    return () => {
      socket.off("Login response", (data) => onResponse(data));
    };
  }, [navigate, socket]);

  const handleChange = (e) => {
    setLogin({
      ...login,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const { email, password } = login;
    if (email !== "" || password !== "") {
      socket.emit("Login", {
        email: email,
        password: password,
      });
    } else {
      setResponse("Preventive");
      setMessage("Incomplete fields, please try again");
      setVisible(true);
      setTimeout(() => {
        setVisible(false);
      }, 3000);
    }
  };

  return (
    <div className=" w-2/4 flex h-full flex-col justify-center">
      <div className=" flex flex-col justify-center items-center">
        <img className=" w-1/4" src={Logo} alt="" />
        <h2 className=" w-auto text-2xl font-bold">Login to your account</h2>
      </div>
    </div>
  );
};

export default Login;
