import React, { useEffect, useState } from "react";
import { Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  Filler,
} from "chart.js";
import { socket } from "../../config/socket";

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  Filler
);

const Chart = ({ type, array, labels, label }) => {
  const [sales, setSales] = useState([]);
  const [products, setProducts] = useState([]);
  const [profit, profits] = useState(0);
  const [dataArrived, setDataArrived] = useState(false);
  useState;
  useEffect(() => {
    if (!dataArrived) {
      function onSaleData(data) {
        setSales(data);
      }

      function onProductsData(data) {
        setProducts(data);
      }

      socket.emit("get_report");
      socket.on("post_report", () => setDataArrived(true));
      socket.on("post_sales", (data) => onSaleData(data));
      socket.on("products", (data) => onProductsData(data));

      return () => {
        socket.off("post_report", () => setDataArrived(true));
        socket.off("post_sales", (data) => onSaleData(data));
        socket.off("products", (data) => onProductsData(data));
      };
    }
  }, [dataArrived]);

  var dataSet = {
    labels: labels ? labels : [],
    datasets: [
      {
        label: `${label}`,
        data: array,
        tension: 0.5,
        fill: true,
        borderColor: "rgb(255,99,132)",
        backgroundColor: "rgba(255,99,132,0.5)",
        pointRadius: 5,
        pointBorderColor: "rgba(255, 99,132)",
        pointBackgroundColor: "rgba(255,99,132)",
      },
    ],
  };

  var optionSet;
  return (
    <div className=" lg:w-3/5 xl:w-4/5 md:w-1/2" style={{ minWidth: "60%" }}>
      <Line data={dataSet} options={optionSet} />;
    </div>
  );
};

export default Chart;
