import { useEffect, useState } from "react";
import "./App.css";
import { socket } from "./config/socket";
import UserCard from "./components/custom/UserCard";

function App() {
  const [newUser, setNewUser] = useState([]);
  useEffect(() => {
    function onConnect() {
      console.log("connected");
    }
    function onDisconnect() {
      console.log("disconnected");
    }
    function onNewUser(data) {
      if (!newUser.includes(data)) setNewUser([...newUser, data]);
    }
    socket.on("connect", onConnect);
    socket.on("disconnect", onDisconnect);
    socket.on("newUser", (data) => onNewUser(data));
    return () => {
      socket.off("connect", onConnect);
      socket.off("disconnect", onDisconnect);
      socket.off("newUser", (data) => onNewUser(data));
    };
  }, [newUser]);

  return (
    <>
      {newUser.map((user, index) => (
        <UserCard key={index} nombre={user.nombre} apellido={user.apellido} />
      ))}
    </>
  );
}

export default App;
