import { useEffect, useState } from "react";
import "./App.css";
import { socket } from "./config/socket";

function App() {
  const [newUser, setNewUser] = useState([]);
  useEffect(() => {
    function onConnect() {
      console.log("connected");
    }
    function onDisconnect() {
      console.log("disconnected");
    }
    function onNewUser(data) {
      if (!newUser.includes(data))
        setNewUser((prevState) => ({ ...prevState, data }));
    }
    socket.on("connect", onConnect);
    socket.on("disconnect", onDisconnect);
    socket.on("newUser", (data) => onNewUser(data));
    return () => {
      socket.off("connect", onConnect);
      socket.off("disconnect", onDisconnect);
      socket.off("newUser", (data) => onNewUser(data));
    };
  }, [newUser]);

  return <>{newUser ? null : newUser.map((user) => console.log(user))}</>;
}

export default App;
